function update(){document.getElementById("result").value=prefix+random+suffix}function generate(){random=document.getElementById("numbers_only").checked?generate_random_number(length):generate_random_hex(length),document.getElementById("result").value=prefix+random+suffix}function generate_random_hex(e){return[...Array(e)].map(()=>Math.floor(16*Math.random()).toString(16)).join("")}function generate_random_number(e){var n=1,t=12-n;if(e>t)return generate_random_number(t)+generate_random_number(e-t);t=Math.pow(10,e+n);var d=t/10,r=Math.floor(Math.random()*(t-d+1))+d;return(""+r).substring(n)}var prefix,suffix,length,random="";document.addEventListener("DOMContentLoaded",function(e){prefix=document.getElementById("prefix").value,suffix=document.getElementById("suffix").value,length=parseInt(document.getElementById("length").value);var n=new bootstrap.Tooltip(document.getElementById("copy"));document.getElementById("generate").addEventListener("click",function(e){generate()}),document.getElementById("prefix").addEventListener("input",function(e){prefix=document.getElementById("prefix").value,update()}),document.getElementById("suffix").addEventListener("input",function(e){suffix=document.getElementById("suffix").value,update()}),document.getElementById("length").addEventListener("input",function(e){length=parseInt(document.getElementById("length").value),generate()}),document.getElementById("numbers_only").addEventListener("change",function(e){generate()}),document.getElementById("copy").addEventListener("click",function(e){document.getElementById("result").select(),document.execCommand("copy"),n.show(),setTimeout(()=>{n.hide()},2e3)})});