function update(){document.getElementById("result").value=prefix+random+suffix}function generate(){random=document.getElementById("numbers_only").checked?generate_random_number(length):generate_random_hex(length),document.getElementById("result").value=prefix+random+suffix}function generate_random_hex(e){return[...Array(e)].map(()=>Math.floor(16*Math.random()).toString(16)).join("")}function generate_random_number(e){var t=1,n=12-t;if(e>n)return generate_random_number(n)+generate_random_number(e-n);n=Math.pow(10,e+t);var d=n/10,r=Math.floor(Math.random()*(n-d+1))+d;return(""+r).substring(t)}var prefix,suffix,length,random="";document.addEventListener("DOMContentLoaded",function(e){prefix=document.getElementById("prefix").value,suffix=document.getElementById("suffix").value,length=parseInt(document.getElementById("length").value);var t=new bootstrap.Tooltip(document.getElementById("copy"),{placement:"top",trigger:"manual",title:"Copied"});document.getElementById("generate").addEventListener("click",function(e){generate()}),document.getElementById("prefix").addEventListener("input",function(e){prefix=document.getElementById("prefix").value,update()}),document.getElementById("suffix").addEventListener("input",function(e){suffix=document.getElementById("suffix").value,update()}),document.getElementById("length").addEventListener("input",function(e){length=parseInt(document.getElementById("length").value),generate()}),document.getElementById("numbers_only").addEventListener("change",function(e){generate()}),document.getElementById("copy").addEventListener("click",function(e){document.getElementById("result").select(),document.execCommand("copy"),t.show(),setTimeout(()=>{t.hide()},2e3)})});